import React, { useState } from "react";
import tshirtImage from "./images/tshirt.jpg";
import hoodieImage from "./images/jumper.jpg";
import hatImage from "./images/hat.jpg";
import bagImage from "./images/bag.jpg";
import mugImage from "./images/mug.jpg";


function Cart() {
  const [cartItems, setCartItems] = useState([]);
  const [cartTotal, setCartTotal] = useState(0);

  function updateCart(item, price, operation) {
    let itemFound = false;
    let newCartItems = cartItems.map((cartItem) => {
      if (cartItem.itemName === item) {
        itemFound = true;
        let quantity = cartItem.quantity;
        if (operation === "add") {
          quantity++;
        } else if (operation === "remove") {
          quantity--;
        }
        let newPrice = quantity * price;
        return {
          itemName: item,
          price: newPrice,
          quantity: quantity,
        };
      } else {
        return cartItem;
      }
    });

    if (!itemFound && operation === "add") {
      newCartItems.push({
        itemName: item,
        price: price,
        quantity: 1,
      });
    }

    setCartItems(newCartItems);
  }

  function removeFromCart(item, price) {
    let newCartItems = cartItems.filter(
      (cartItem) => cartItem.itemName !== item
    );
    setCartItems(newCartItems);
  }

  function calculateTotal() {
    let total = 0;
    cartItems.forEach((cartItem) => {
      total += cartItem.price;
    });
    setCartTotal(total);
  }

  function handleAddToCartClick(item, price) {
    updateCart(item, price, "add");
    calculateTotal();
  }

  function handleRemoveFromCartClick(item, price) {
    updateCart(item, price, "remove");
    calculateTotal();
  }

  return (
<div>
  <section id="merchandise">
    <h1 style={{ color: "#a18525" }}>Merchandise</h1>
    <table style="margin: 0 auto;">
      <thead>
        <tr>
          <th>Item</th>
          <th>Image</th>
          <th>Price</th>
          <th>Add to Cart</th>
        </tr>
      </thead>
      <tbody>
        <tr>
          <td>T-Shirt</td>
          <td><img src={tshirtImage} alt="T-Shirt" /></td>
          <td>$35</td>
          <td><button onClick={() => handleAddToCartClick("T-Shirt", 35)}>Add to Cart</button></td>
        </tr>
        <tr>
          <td>Hoodie</td>
          <td><img src={hoodieImage} alt="Hoodie" /></td>
          <td>$50</td>
          <td><button onClick={() => handleAddToCartClick("Hoodie", 50)}>Add to Cart</button></td>
        </tr>
        <tr>
          <td>Hat</td>
          <td><img src={hatImage} alt="Hat" /></td>
          <td>$25</td>
          <td><button onClick={() => handleAddToCartClick("Hat", 25)}>Add to Cart</button></td>
        </tr>
        <tr>
          <td>Bag</td>
          <td><img src={bagImage} alt="Bag" /></td>
          <td>$60</td>
          <td><button onClick={() => handleAddToCartClick("Bag", 60)}>Add to Cart</button></td>
        </tr>
        <tr>
          <td>Mug</td>
          <td><img src={mugImage} alt="Mug" /></td>
          <td>$15</td>
          <td><button onClick={() => handleAddToCartClick("Mug", 15)}>Add to Cart</button></td>
        </tr>
      </tbody>
    </table>
    <h2>Cart</h2>
    <table>
      <thead>
        <tr>
          <th>Item</th>
          <th>Price</th>
          <th>Quantity</th>
          <th>Remove from Cart</th>
        </tr>
      </thead>
      <tbody>
        {cartItems.map((cartItem) => (
          <tr key={cartItem.itemName}>
            <td>{cartItem.itemName}</td>
            <td>${cartItem.price}</td>
            <td>{cartItem.quantity}</td>
            <td>
              <button onClick={() => handleRemoveFromCartClick(cartItem.itemName, cartItem.price)}>
                Remove from Cart
              </button>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
    <div>Total: ${cartTotal}</div>
    <br />
    <br />
    <div className="back-to-top">
      <a href="#navigation" className="back-to-top">
        Back to top
      </a>
    </div>
  </section>
</div>

  );
}

export default Cart;
