import React, { useState } from 'react';

function ShoppingCart() {
  const [cartItems, setCartItems] = useState([]);
  const [cartTotal, setCartTotal] = useState(0);

  function updateCart(item, price) {
    // Look for the item in the cart
    let itemFound = false;
    const updatedCartItems = cartItems.map((cartItem) => {
      if (cartItem.itemName === item) {
        itemFound = true;
        const updatedCartItem = {
          ...cartItem,
          quantity: cartItem.quantity + 1,
          itemPrice: cartItem.itemPrice + price,
        };
        return updatedCartItem;
      }
      return cartItem;
    });

    // If the item is not found, create a new cart item
    if (!itemFound) {
      const newCartItem = {
        itemName: item,
        itemPrice: price,
        quantity: 1,
      };
      updatedCartItems.push(newCartItem);
    }

    // Update the cart items and total
    setCartItems(updatedCartItems);
    setCartTotal(cartTotal + price);
  }

  return (
    <div>
      <h2>Shopping Cart</h2>
      <div id="cart">
        {cartItems.map((cartItem, index) => (
          <div key={index} className="cart-item">
            <div className="cart-item-name">{cartItem.itemName}</div>
            <div className="cart-item-price">${cartItem.itemPrice.toFixed(2)}</div>
            <div className="cart-item-quantity">{cartItem.quantity}</div>
          </div>
        ))}
      </div>
      <div id="cart-total">Total: ${cartTotal.toFixed(2)}</div>
      <button onClick={() => updateCart('Item 1', 10)}>Add Item 1 to Cart</button>
      <button onClick={() => updateCart('Item 2', 20)}>Add Item 2 to Cart</button>
    </div>
  );
}

export default ShoppingCart;
