import { useState, useEffect } from 'react';

function NewsReel() {
  const [newsItems, setNewsItems] = useState([]);

  useEffect(() => {
    // Fetch news items from API or hardcode them
    setNewsItems([
      { id: 1, title: 'Breaking news 1' },
      { id: 2, title: 'Breaking news 2' },
      { id: 3, title: 'Breaking news 3' },
      { id: 4, title: 'Breaking news 4' },
      { id: 5, title: 'Breaking news 5' },
      { id: 6, title: 'Breaking news 6' },
    ]);
  }, []);

  useEffect(() => {
    // Scroll the container every 10 milliseconds
    const container = document.getElementById('news-reel-container');
    let interval = null;

    const startScrolling = () => {
      interval = setInterval(() => {
        container.scrollLeft += 2;
      }, 10);
    };

    const stopScrolling = () => {
      clearInterval(interval);
    };

    container.addEventListener('mouseenter', stopScrolling);
    container.addEventListener('mouseleave', startScrolling);

    startScrolling();

    return () => {
      clearInterval(interval);
      container.removeEventListener('mouseenter', stopScrolling);
      container.removeEventListener('mouseleave', startScrolling);
    };
  }, []);

  return (
    <div
      id="news-reel-container"
      style={{
        display: 'flex',
        alignItems: 'center',
        overflowX: 'scroll',
        height: '80px',
        whiteSpace: 'nowrap',
        padding: '10px',
        backgroundColor: '#f5f5f5',
        boxShadow: 'inset 0 0 10px rgba(0, 0, 0, 0.1)',
      }}
    >
      {newsItems.map((item) => (
        <div
          key={item.id}
          style={{
            display: 'inline-block',
            width: '300px',
            margin: '0 10px',
            backgroundColor: '#fff',
            borderRadius: '10px',
            padding: '10px',
            boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
            overflow: 'hidden',
            textOverflow: 'ellipsis',
          }}
        >
          {item.title}
        </div>
      ))}
    </div>
  );
}

export default NewsReel;
