import { useState, useEffect } from 'react';

function NewsReel() {
  const [newsItems, setNewsItems] = useState([]);
  const [numArticles, setNumArticles] = useState(6);

  useEffect(() => {
    // Fetch news items from GNews API
    const apikey = 'YOUR_API_KEY';
    const url = `https://gnews.io/api/v4/search?q=netball&lang=en&country=au&max=${numArticles}&apikey=${apikey}`;

    fetch(url)
      .then((response) => response.json())
      .then((data) =>
        setNewsItems(
          data.articles.map((article) => ({
            id: article.url,
            title: article.title,
            description: article.description,
            url: article.url,
            image: article.image,
            content: article.content
          }))
        )
      );
  }, [numArticles]);

  const handleLoadMore = () => {
    setNumArticles(numArticles + 6);
  }

  return (
    <div
      id="news-reel-container"
      style={{
        display: 'flex',
        alignItems: 'center',
        overflowX: 'scroll',
        height: '80px',
        whiteSpace: 'nowrap',
        padding: '10px',
        backgroundColor: '#f5f5f5',
        boxShadow: 'inset 0 0 10px rgba(0, 0, 0, 0.1)',
      }}
    >
      {newsItems.map((item) => (
        <a
          key={item.id}
          href={item.url}
          target="_blank"
          rel="noopener noreferrer"
          style={{
            display: 'inline-block',
            width: '300px',
            margin: '0 10px',
            backgroundColor: '#fff',
            borderRadius: '10px',
            padding: '10px',
            boxShadow: '0 0 10px rgba(0, 0, 0, 0.1)',
            overflow: 'hidden',
            textOverflow: 'ellipsis',
            textDecoration: 'none',
            color: '#000',
          }}
        >
          {item.image && (
            <img
              src={item.image}
              alt={item.title}
              style={{ width: '100%', height: '150px', objectFit: 'cover' }}
            />
          )}
          <div style={{ fontWeight: 'bold', marginBottom: '10px' }}>{item.title}</div>
          <div>{item.description}</div>
          <div>{item.content}</div>
        </a>
      ))}
      {numArticles < 30 && (
        <button onClick={handleLoadMore} style={{ margin: '0 10px', backgroundColor: '#fff', border: '1px solid #000', borderRadius: '10px', padding: '10px' }}>
          Load More
        </button>
      )}
    </div>
  );
}

export default NewsReel;
