import React, { useState } from 'react';

const itemsToPurchase = [
  { name: 'T-shirt', price: 20 },
  { name: 'Hoodie', price: 40 },
  { name: 'Cap', price: 15 },
  // add more items here...
];

function ShoppingCart() {
  const [cartTotal, setCartTotal] = useState(0);
  const [cartItems, setCartItems] = useState([]);

  function updateCart(item, price) {
    // Look for the item in the cart
    const existingItem = cartItems.find(cartItem => cartItem.name === item);
    if (existingItem) {
      // If the item is found, update the quantity and price
      const updatedItem = { ...existingItem, quantity: existingItem.quantity + 1 };
      setCartItems(cartItems.map(cartItem => cartItem.name === item ? updatedItem : cartItem));
      setCartTotal(cartTotal + price);
    } else {
      // If the item is not found, create a new cart item
      const newItem = { name: item, price: price, quantity: 1 };
      setCartItems([...cartItems, newItem]);
      setCartTotal(cartTotal + price);
    }
  }

  return (
    <div>
      <h1>Shopping Cart</h1>
      <div>
        {itemsToPurchase.map(item => (
          <button key={item.name} onClick={() => updateCart(item.name, item.price)}>
            Add {item.name} to Cart - ${item.price.toFixed(2)}
          </button>
        ))}
      </div>
      <h2>Cart Items:</h2>
      <div id="cart">
        {cartItems.map((item, index) => (
          <div key={index} className="cart-item">
            <div className="cart-item-name">{item.name}</div>
            <div className="cart-item-price">${(item.price * item.quantity).toFixed(2)}</div>
            <div className="cart-item-quantity">{item.quantity}</div>
          </div>
        ))}
      </div>
      <h2>Cart Total: ${cartTotal.toFixed(2)}</h2>
    </div>
  );
}

export default ShoppingCart;
