import React, { useState } from 'react';

const itemsToPurchase = [
  { name: 'T-shirt', price: 20 },
  { name: 'Hoodie', price: 40 },
  { name: 'Cap', price: 15 },
  // add more items here...
];

function ShoppingCart() {
  const [cartItems, setCartItems] = useState([]);
  const [cartTotal, setCartTotal] = useState(0);

  function updateCart(item, price) {
    // Look for the item in the cart
    const itemIndex = cartItems.findIndex((cartItem) => cartItem.name === item);
    if (itemIndex >= 0) {
      // If the item is found, update the quantity and price
      const updatedCartItems = [...cartItems];
      const updatedItem = { ...updatedCartItems[itemIndex] };
      updatedItem.quantity++;
      updatedItem.price = updatedItem.quantity * price;
      updatedCartItems[itemIndex] = updatedItem;
      setCartItems(updatedCartItems);
    } else {
      // If the item is not found, create a new cart item
      const newCartItem = { name: item, price: price, quantity: 1 };
      setCartItems([...cartItems, newCartItem]);
    }

    // Update the cart total
    const newCartTotal = cartTotal + price;
    setCartTotal(newCartTotal.toFixed(2));
  }

  function removeCartItem(item) {
    const itemIndex = cartItems.findIndex((cartItem) => cartItem.name === item);
    if (itemIndex >= 0) {
      const updatedCartItems = [...cartItems];
      const updatedItem = { ...updatedCartItems[itemIndex] };
      const itemPrice = updatedItem.price;
      updatedCartItems.splice(itemIndex, 1);
      setCartItems(updatedCartItems);
      const newCartTotal = cartTotal - itemPrice;
      setCartTotal(newCartTotal.toFixed(2));
    }
  }

  return (
    <div>
      <h2>Shopping Cart</h2>
      <div id="cart">
        {cartItems.map((cartItem) => (
          <div key={cartItem.name} className="cart-item">
            <div className="cart-item-name">{cartItem.name}</div>
            <div className="cart-item-price">{cartItem.price.toFixed(2)}</div>
            <div className="cart-item-quantity">{cartItem.quantity}</div>
            <button onClick={() => removeCartItem(cartItem.name)}>Remove</button>
          </div>
        ))}
      </div>
      <div id="cart-total">Total: ${cartTotal}</div>
      <div>
        <h3>Items to Purchase</h3>
        <ul>
          <li onClick={() => updateCart("Shirt", 20)}>Shirt - $20</li>
          <li onClick={() => updateCart("Hat", 10)}>Hat - $10</li>
          <li onClick={() => updateCart("Pants", 30)}>Pants - $30</li>
        </ul>
      </div>
    </div>
  );
}

export default ShoppingCart;
