import React, { useState } from 'react';
import { Provider } from 'react-redux';
import store from './store';
import TicketForm from './TicketForm';
import MembershipForm from './MembershipForm';

const TicketMembershipForm = () => {
  const [activeForm, setActiveForm] = useState('tickets');

  const handleSubmit = (event) => {
    event.preventDefault();
    // Handle form submission based on activeForm value
    if (activeForm === 'tickets') {
      // Handle ticket form submission
      console.log(`Submitting ticket form with email: ${event.target.email.value}`);
      // Display success message
      alert('Your ticket/s have been sent to your email.');
    } else {
      // Handle membership form submission
      console.log(`Submitting membership form with email: ${event.target.email.value}`);
      // Display success message
      alert('Thank you, we will be in touch.');
    }
  };

  const handleTabClick = (form) => {
    setActiveForm(form);
  };

  return (
    <Provider store={store}>
      <div>
        <div>
          <button
            onClick={() => handleTabClick('tickets')}
            className={activeForm === 'tickets' ? 'active' : ''}
          >
            Purchase Tickets
          </button>
          <button
            onClick={() => handleTabClick('membership')}
            className={activeForm === 'membership' ? 'active' : ''}
          >
            Become a Member
          </button>
        </div>
        <form onSubmit={handleSubmit}>
          {activeForm === 'tickets' ? (
            <TicketForm handleSubmit={handleSubmit} />
          ) : (
            <MembershipForm handleSubmit={handleSubmit} />
          )}
          <button type="submit">
            {activeForm === 'tickets' ? 'Purchase Tickets' : 'Join Now'}
          </button>
        </form>
      </div>
    </Provider>
  );
};

export default TicketMembershipForm;
