import React, { useState } from 'react';
import Fixture from './Fixture';
import Results from './Results';
import Ladder from './Ladder';
import './MatchCentre.css';

const MatchCentre = () => {
  const [activeTab, setActiveTab] = useState('fixture');

  const handleTabClick = (tab) => {
    setActiveTab(tab);
  };

  return (
    <div className="match-centre">
      <div className="tabs">
        <button
          className={`tab ${activeTab === 'fixture' ? 'active' : ''}`}
          onClick={() => handleTabClick('fixture')}
        >
          Fixture
        </button>
        <button
          className={`tab ${activeTab === 'results' ? 'active' : ''}`}
          onClick={() => handleTabClick('results')}
        >
          Results
        </button>
        <button
          className={`tab ${activeTab === 'ladder' ? 'active' : ''}`}
          onClick={() => handleTabClick('ladder')}
        >
          Ladder
        </button>
      </div>
      <div className="tab-content">
        {activeTab === 'fixture' && <Fixture />}
        {activeTab === 'results' && <Results />}
        {activeTab === 'ladder' && <Ladder />}
      </div>
    </div>
  );
};
