import React from 'react';
import './css/styles.css';

const profiles = [
  // ... player profile objects here
];

const ProfileCard = ({ profile }) => {
  return (
    <div className="profile-card">
      <img className="profile-image" src={profile.image} alt={profile.name} />
      <div className="profile-content">
        <h2>{profile.name}</h2>
        <p>{profile.description}</p>
        <ul>
          {profile.stats.map(stat => (
            <li key={stat.label}>
              <strong>{stat.label}:</strong> {stat.value}
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

const TabbedProfiles = () => {
  const [activeTab, setActiveTab] = useState(0);

  const handleTabClick = tabIndex => {
    setActiveTab(tabIndex);
  };

  const activeProfile = profiles[activeTab];

  return (
    <div className="tabbed-profiles">
      <div className="tab-list">
        {profiles.map((profile, index) => (
          <button
            key={profile.id}
            className={`tab-button ${index === activeTab ? 'active' : ''}`}
            onClick={() => handleTabClick(index)}
          >
            {profile.name}
          </button>
        ))}
      </div>
      <div className="profile-container">
        <div className={`profile-card ${activeProfile ? 'fade-in' : 'fade-out'}`}>
          {activeProfile && <ProfileCard profile={activeProfile} />}
        </div>
      </div>
    </div>
  );
};

export default TabbedProfiles;
